    private static final long[] c = new long[64];
 
    public static void main(String[] args) {
        for (int n = 0; n < 10; n++) {
            coeff(n);
            show(n);
        }
 
        System.out.print("Primes:");
        for (int n = 1; n < c.length; n++)
