public class Heron {
    public static void main(String[] args) {
        ArrayList<int[]> list = new ArrayList<>();
 
        for (int c = 1; c <= 200; c++) {
            for (int b = 1; b <= c; b++) {
                for (int a = 1; a <= b; a++) {
 
                    if (gcd(gcd(a, b), c) == 1 && isHeron(heronArea(a, b, c))){
