import random
from collections import Counter

class SOfN:
    def __init__(self, n):
        self.n = n
        self.sample = []
        self.i = 0

    def process(self, item):
        self.i += 1
        if self.i <= self.n:
            self.sample.append(item)
        else:
            if random.randint(0, self.i - 1) < self.n:
                self.sample[random.randint(0, self.n - 1)] = item
        return self.sample

def s_of_n_creator(n):
    s_of_n_instance = SOfN(n)
    return s_of_n_instance.process

def test_s_of_n():
