import org.junit.jupiter.api.Assertions.assertEquals
import org.junit.jupiter.api.Test

class OneHundredDoorsTest {

    @Test
    fun testOneHundredDoors() {
        val expectedOpenDoors = listOf(1, 4, 9, 16, 25, 36, 49, 64, 81, 100)
        val result = oneHundredDoors()
        assertEquals(expectedOpenDoors, result, "The open doors should be those with perfect square numbers.")
    }

    @Test
    fun testNoExtraOpenDoors() {
        val result = oneHundredDoors()
        val nonPerfectSquares = (1..100).filterNot { isPerfectSquare(it) }
        val closedDoors = nonPerfectSquares.filterNot { result.contains(it) }
        assertEquals(nonPerfectSquares, closedDoors, "All non-perfect square doors should be closed.")
    }

    private fun isPerfectSquare(number: Int): Boolean {
        val sqrt = Math.sqrt(number.toDouble()).toInt()
        return sqrt * sqrt == number
    }
}
