    // Helper function to check if a number is k-almost prime
    private fun Int.k_prime(x: Int): Boolean {
        var n = x
        var f = 0
        var p = 2
        while (f < this && p * p <= n) {
            while (0 == n % p) {
                n /= p
                f++
            }
            p++
        }
        return f + (if (n > 1) 1 else 0) == this
    }

