import unittest

def gcd(a, b):
    while a != b:
        if a > b:
            a -= b
        else:
            b -= a
    return a

def generate_ekg_sequence(start, limit):
    sequence = [1, start]
    used_numbers = set(sequence)

    for n in range(2, limit):
        i = 2
        while True:
            if i not in used_numbers and gcd(sequence[n - 1], i) > 1:
                sequence.append(i)
                used_numbers.add(i)
                break
            i += 1
    return sequence

class TestEKGSequence(unittest.TestCase):

    def test_ekg_2(self):
        expected = [1, 2, 4, 6, 3, 9, 12, 8, 10, 5]
        result = generate_ekg_sequence(2, 10)
        self.assertEqual(result, expected)

    def test_ekg_5(self):
        expected = [1, 5, 10, 2, 4, 6, 3, 9, 12, 8]
        result = generate_ekg_sequence(5, 10)
        self.assertEqual(result, expected)

    def test_ekg_7(self):
        expected = [1, 7, 14, 2, 4, 6, 3, 9, 12, 8]
        result = generate_ekg_sequence(7, 10)
        self.assertEqual(result, expected)

    def test_ekg_9(self):
        expected = [1, 9, 3, 6, 2, 4, 8, 10, 5, 15]
        result = generate_ekg_sequence(9, 10)
        self.assertEqual(result, expected)

    def test_ekg_10(self):
        expected = [1, 10, 5, 15, 3, 9, 6, 2, 4, 8]
        result = generate_ekg_sequence(10, 10)
        self.assertEqual(result, expected)

    def test_convergence_ekg_5_and_7(self):
        ekg_5 = generate_ekg_sequence(5, 100)
        ekg_7 = generate_ekg_sequence(7, 100)
        convergence_term = None
        for i in range(2, 100):
            if ekg_5[i] == ekg_7[i] and sorted(ekg_5[:i]) == sorted(ekg_7[:i]):
                convergence_term = i + 1
                break
        self.assertEqual(convergence_term, 8)

if __name__ == '__main__':
    unittest.main()
