```python
import unittest

def gcd(a, b):
    while a != b:
        if a > b:
            a -= b
        else:
            b -= a
    return a

def generate_ekg_sequence(start, limit=10):
    sequence = [1, start]
    used_numbers = set(sequence)

    while len(sequence) < limit:
        n = sequence[-1]
        candidate = 2
        while True:
            if candidate not in used_numbers and gcd(n, candidate) > 1:
                sequence.append(candidate)
                used_numbers.add(candidate)
                break
            candidate += 1
    return sequence

def find_convergence(ekg1, ekg2, limit=100):
    for i in range(2, limit):
        if ekg1[i] == ekg2[i] and sorted(ekg1[:i]) == sorted(ekg2[:i]):
            return i + 1
    return None

class TestEKGSequence(unittest.TestCase):

    def test_ekg_2(self):
        expected = [1, 2, 4, 6, 3, 9, 12, 8, 10, 5]
        result = generate_ekg_sequence(2, 10)
        self.assertEqual(result, expected)

    def test_ekg_5(self):
        expected = [1, 5, 10, 2, 4, 6, 3, 9, 12, 8]
        result = generate_ekg_sequence(5, 10)
        self.assertEqual(result, expected)

    def test_ekg_7(self):
        expected = [1, 7, 14, 2, 4, 6, 3, 9, 12, 8]
        result = generate_ekg_sequence(7, 10)
        self.assertEqual(result, expected)

    def test_ekg_9(self):
        expected = [1, 9, 3, 6, 2, 4, 8, 10, 5, 15]
        result = generate_ekg_sequence(9, 10)
        self.assertEqual(result, expected)

    def test_ekg_10(self):
        expected = [1, 10, 5, 15, 3, 9, 6, 2, 4, 8]
        result = generate_ekg_sequence(10, 10)
        self.assertEqual(result, expected)

    def test_convergence_5_7(self):
        ekg_5 = generate_ekg_sequence(5, 100)
        ekg_7 = generate_ekg_sequence(7, 100)
        convergence_term = find_convergence(ekg_5, ekg_7, 100)
        self.assertEqual(convergence_term, 15)

if __name__ == '__main__':
    unittest.main()
```

