        self.assertEqual(pRes(CF(sqrt2_a(), sqrt2_b(), 950), 10), "1.4142135623")

    def test_Napier(self):
        self.assertEqual(pRes(CF(Napier_a(), Napier_b(), 950), 10), "2.7182818284")

    def test_Pi(self):
