     sys.stdout.flush()
 
 
class Ball():
    def __init__(self):
        self.x = 0
        self.y = 0
 
    def update(self):
        self.x += random.randint(0,1)
        self.y += 1
 
    def fall(self):
        self.y +=1
 
 
class Board():
    def __init__(self, width, well_depth, N):
        self.balls = []
        self.fallen = [0] * (width + 1)
        self.width = width
        self.well_depth = well_depth
        self.N = N
        self.shift = 4
 
