import unittest
from md5_implementation import md5, md5_to_hex

class TestMD5(unittest.TestCase):

    def test_empty_string(self):
        self.assertEqual(md5_to_hex(md5(b"")), "d41d8cd98f00b204e9800998ecf8427e")

