def xor(a, b): return (a and not b) or (b and not a)
 
def ha(a, b): return xor(a, b), a and b     # sum, carry
 
def fa(a, b, ci):
    s0, c0 = ha(ci, a)
    s1, c1 = ha(s0, b)
    return s1, c0 or c1     # sum, carry
 
def fa4(a, b):
    width = 4
    ci = [None] * width
    co = [None] * width
    s  = [None] * width
    for i in range(width):
        s[i], co[i] = fa(a[i], b[i], co[i-1] if i else 0)
    return s, co[-1]
 
def int2bus(n, width=4):
